css:
.order-card { 
    border-radius: 8px;
    margin-bottom: 10px;
    box-shadow: 0 4px 8px rgba(0,0,0,0.1); /* Box shadow effect */
}

.first-row { 
    display: flex;  
    justify-content: space-between;
    padding: 8px 10px;
}

.first-row p { 
    margin-bottom: 0px;
}

.first-row p:nth-child(1) { 
    color: #8a8a8a;
}

.first-row p:nth-child(2) { 
    font-weight: 600;
}

.second-row {
    padding: 8px 10px;
    background-color: #f2f3f5;
    display: flex;
    align-items: center;
}

.second-row p {
    margin-bottom: 0px;
    font-weight: 400;
    font-size: 18px;
}

.third-row {
    display: flex;  
    justify-content: space-between;
    padding: 8px 10px;
}

.third-row p {
    margin-bottom: 0px;
}

.confirmed { 
    background-color: green;
}

.status-wrapper p {
    color: white;
    font-weight: 600;
}

.status-wrapper {
    padding: 4px 6px;
    display: inline-block;
    border-radius: 12px;
}

.cancelled { 
    background-color: red;

}

.print-button {
    display: inline-block;
    padding: 4px 6px;
    background-color: #007bff; /* Blue color, you can change this */
    color: #fff; /* Text color */
    border: none;
    border-radius: 4px;
    cursor: pointer;
    text-align: center;
    text-decoration: none;
    transition: background-color 0.3s ease;
    font-size: 16px; /* Adjust font size as needed */
}

.print-button:hover {
    background-color: #0056b3; /* Darker shade of the button color on hover */
}

