$(document).ready(function() {
    $.ajax({
        url: 'http://event.localhost:8000/api/method/event_management.api.get_orders_by_user',
        method: 'GET',
        success: function(response) {
            if (response.message.success_key === 1) {
                const orders = response.message.data;
                let orderListHtml = '';

                if (orders.length > 0) {
                    orders.forEach(order => {
                        const statusClass = order.status.toLowerCase() === 'confirmed' ? 'confirmed' : 'cancelled';
                        const myFOrderId = order.name;

                        orderListHtml += `
                            <div class="order-card">
                                <div class="first-row">
                                    <p class="order-text">Event ID ${order.event_id}</p>
                                    <p class="order-text">Rp ${order.total_price.toLocaleString('id-ID')}</p>
                                </div>
                                <div class="second-row">                                
                                    <p class="order-text">${order.event_title}</p>
                                </div>
                                <div class="third-row">
                                    <div class="status-wrapper ${statusClass}">
                                        <p>${order.status}</p>
                                    </div>
                                    <button class="print-button" data-order-id="${order.name}">Print Receipt</button>
                                </div>
                            </div>
                        `;
                    });
                } else {
                    orderListHtml = '<p>No orders found.</p>';
                }

                $('#order-list').html(orderListHtml);

                // Add event listener for print buttons
                $('.print-button').on('click', function() {
                    const orderId = $(this).data('order-id');
                    if (!orderId) {
                        alert('Order ID is missing.');
                        return;
                    }
                    const doctype = 'Orders'; // Replace with your DocType
                    const print_format = 'Order Print'; // Replace with your print format name

                    // Construct the URL to print the document
                    const print_url = `/printview?doctype=${doctype}&name=${orderId}&format=${print_format}&no_letterhead=0&_lang=en`;

                    // Open the print view in a new window
                    window.open(print_url, '_blank');
                });
            } else {
                $('#order-list').html('<p>Failed to fetch orders.</p>');
            }
        },
        error: function() {
            $('#order-list').html('<p>Failed to fetch orders.</p>');
        }
    });
});

