$(document).ready(function() {
    $.ajax({
        url: 'http://event.localhost:8000/api/method/event_management.api.get_events',
        method: 'GET',
        success: function(response) {
            if(response.message.success_key === 1) {
                const events = response.message.data;
                let eventListHtml = '';

                events.forEach(event => {
                    const statusClass = event.status.toLowerCase() === 'available' ? 'available' : 'sold-out';

                    eventListHtml += `
                        <div class="event-card">
                            <img src="${event.image}" class="card-img-top" alt="${event.event_title}">
                            <div class="card-body">
                            <h5 class="card-title">${event.event_title}</h5>
                            <p class="card-text"><strong>Organized By:</strong> ${event.organized_by}</p>
                            <p class="card-text"><strong>Starts on:</strong> ${new Date(event.starts_on).toLocaleString()}</p>
                                <p class="card-text"><strong>Ends on:</strong> ${new Date(event.ends_on).toLocaleString()}</p>
                                <p class="card-text">${event.location}</p>
                                <p class="card-text"><strong>Price:</strong> Rp ${event.price.toLocaleString('id-ID')}</p>
                                <div class="status-container">
                                    <p class="card-text status ${statusClass}">${event.status}</p>
                                    <button class="details-button" onclick="seeDetails('${event.name}')">See Details</button>
                                </div>
                            </div>
                        </div>
                    `;
                });

                $('#event-list').html(eventListHtml);
            } else {
                $('#event-list').html('<p>No events found.</p>');
            }
        },
        error: function() {
            $('#event-list').html('<p>Failed to fetch events.</p>');
        }
    });
});

function seeDetails(eventId) {
