$(document).ready(function() {
    const urlParams = new URLSearchParams(window.location.search);
    const eventId = urlParams.get('event_id');

    if (eventId) {
        $.ajax({
            url: `http://event.localhost:8000/api/method/event_management.api.get_event_by_id?id=${eventId}`,
            method: 'GET',
            data: { event_id: eventId },
            success: function(response) {
                console.log("ini response", response);
                if (response.message.success_key === 1) {
                    const event = response.message.data;
                    console.log("Event response", event);

                    const eventDetailsHtml = `
                        <img src="${event.image}" class="card-img-top" alt="${event.event_title}">
                        <div class="card-body">
                            <h5 class="card-title">${event.event_title}</h5>
                            <p class="card-text"><strong>Starts on:</strong> ${new Date(event.starts_on).toLocaleString()}</p>
                            <p class="card-text"><strong>Ends on:</strong> ${new Date(event.ends_on).toLocaleString()}</p>
                            <p class="card-text"><strong>Organized by:</strong> ${event.organized_by}</p>
                            <p class="card-text"><strong>Location:</strong> ${event.location}</p>
                            <p class="card-text"><strong>Price:</strong> Rp ${event.price.toLocaleString('id-ID')}</p>
                            <p class="card-text"><strong>Status:</strong> ${event.status}</p>
                            <p class="card-text"><strong>Description:</strong> ${event.description}</p>
                            <p class="card-text"><strong>Number of Tickets:</strong> ${event.number_of_tickets}</p>
                            <div class="quantity-control">
                                <button id="decrease-quantity" class="btn btn-secondary">-</button>
                                <span id="order-quantity">1</span>
                                <button id="increase-quantity" class="btn btn-secondary">+</button>
                            </div>
                            <button id="submit-order" class="btn btn-primary mt-3">Submit Order</button>
                        </div>
                    `;

                    $('#event-details').html(eventDetailsHtml);

                    // Quantity control functionality
                    let quantity = 1;
                    $('#increase-quantity').on('click', function() {
                        quantity++;
                        $('#order-quantity').text(quantity);
                    });

                    $('#decrease-quantity').on('click', function() {
                        if (quantity > 1) {
                            quantity--;
                            $('#order-quantity').text(quantity);
                        }
                    });

                    // Order submission functionality
                    $('#submit-order').on('click', function() {
                        $.ajax({
                            url: `http://event.localhost:8000/api/method/event_management.api.create_order`,
                            method: 'POST',
                            contentType: 'application/json',
                            headers: {
                                "X-Frappe-CSRF-Token": frappe.csrf_token,
                            },
                            data: JSON.stringify({ event_id: eventId, total_ticket: quantity }),

                            success: function(response) {
                                alert('Order submitted successfully!');
                                window.location.href = "/my_order";
                            },
                            error: function(xhr) {
                                let response = JSON.parse(xhr.responseText)
                                alert('Failed to submit order.' + response.message.message);
                            }
                        });
                    });
                } else {
                    $('#event-details').html('<p>Event not found.</p>');
                }
            },
            error: function() {
                $('#event-details').html('<p>Failed to fetch event details.</p>');
            }
        });
    } else {
        $('#event-details').html('<p>No event ID provided.</p>');
    }
});
